module.exports = function(client, opt) {
	client.on('message', function(send, tar, msg) {
		if(send != opt.allow) return;
		try {
			var m = msg.match(/!(.*?) (.*?) (.*?)$/);
			if(!m) return;
			client.log('admin','Trying to execute command "'+m[1]+'", with params "'+m[2]+'" and "'+m[3]+'"');
			client[m[1]](m[2],m[3]);
		} catch(e) {
			console.log(e);
		}
	})
}