module.exports=function(client){
	client.on('command:cica',function(send,tar){
		var start=new Date();
		var end=new Date();
		var now=new Date();
		start.setHours(1);
		start.setMinutes(8);
		start.setSeconds(0);
		end.setHours(8);
		end.setMinutes(8);
		end.setSeconds(0);
		if(now.getTime() > start.getTime() && now.getTime() < end.getTime())
		{
			command.getOriginal().reply('Cicafold van! :3');
			return;
		}
		if(now.getTime() > start.getTime())
		{
			start=new Date(start.getTime()+24*60*60*1000);
		}
		var time=start.getTime()-now.getTime();
		var remaining='Cicafoldig meg '+
			Math.floor(time/1000/60/60)+' ora, '+
			Math.floor(time/1000/60%60)+' perc, '+
			Math.floor(time/1000%60)+' masodperc van.';
		client.reply(send,tar,remaining);
	})
}