module.exports = function(client) {
	client.on('command:betoccintottam', function(s, t) {
		var d = new Date().getTime();
		client.db.upsert('toccintas', {
			nick: s
		}, {
			$inc: {
				toccintasok: 1
			},
			$set: {
				last: d
			}
		}, {
			nick: s,
			last: d,
			toccintasok: 1,
			first: d
		}, function(err, res, up) {
			if(err) {
				return console.log(err);
			}
			console.log(err, res);
			var tocc;
			if(up) {
				tocc = res.toccintasok + 1;
			} else {
				tocc = res[0].toccintasok;
			}
			client.reply(s, t, 'Ez a' + (tocc === 1 ? 'z' : '') + ' ' + tocc + '. betoccintasod, gratulalunk!');
		})
	})
}