module.exports=function(client){
	function getUptime()
	{
		var t=Math.floor(process.uptime());
		var days=Math.floor(t/60/60/24);
		var hours=Math.floor(t/60/60%24);
		var minutes=Math.floor(t/60%60);
		var seconds=Math.floor(t%60);
		var up=days+':'+(hours<10?'0':'')+hours+':'+(minutes<10?'0':'')+minutes+':'+(seconds<10?'0':'')+seconds;
		return up;
	}
	client.on('command:uptime',function(send,tar,par){
		client.reply(send,tar,':OUptime is '+getUptime());
	})
}